#include <iostream>
#include <vector>
#include <queue>
using namespace std;

class Solution
{
public:
    int dx[8] = {1, 1, 1, 0, 0, -1, -1, -1};
    int dy[8] = {-1, 1, 0, 1, -1, 0, 1, -1};
    vector<vector<char>> updateBoard(vector<vector<char>> &board, vector<int> &click)
    {
        vector<vector<int> > filledBoard(board.size(), vector<int>(board[0].size(), 0));
        vector<vector<bool> > visited(board.size(), vector<bool>(board[0].size(), false));
        //Fill the board
        for (int i=0; i<board.size(); i++)
        {
            for (int j=0; j<board[i].size(); j++)
            {
                if (board[i][j] == 'M')
                {
                    for (int k = 0; k<8; k++)
                    {
                        int nx = dx[k] + i;
                        int ny = dy[k] + j;
                        if (nx < 0 || ny < 0 || nx >= board.size() || ny >= board[0].size())
                        {
                            continue;
                        }
                        filledBoard[nx][ny]++;
                    }
                }
            }
        }

        int pos = 0;
        int sx = click[0];
        int sy = click[1];
        queue<pair<int, int>> q;
        q.push({sx, sy});
        visited[sx][sy] = true;
        if (board[sx][sy] == 'M')
        {
            board[sx][sy] = 'X';
            return board;
        }

        while (!q.empty())
        {
            pair<int, int> cur = q.front();
            q.pop();

            int aroundMines = filledBoard[cur.first][cur.second];
            board[cur.first][cur.second] = ('0' + aroundMines);

            if (aroundMines == 0)
            {
                board[cur.first][cur.second] = 'B';
                for (int j = 0; j < 8; j++)
                {
                    int nx = dx[j] + cur.first;
                    int ny = dy[j] + cur.second;
                    if (nx < 0 || ny < 0 || nx >= board.size() || ny >= board[0].size() || visited[nx][ny])
                    {
                        continue;
                    }
                    visited[nx][ny] = true;
                    q.push({nx, ny});
                }
            }
        }
        return board;
    }
};

